/*Table structure for table `log` */
DROP TABLE IF EXISTS `log`;
CREATE TABLE `log` (
  `ID` BIGINT(64) UNSIGNED NOT NULL,
  `LOG_TYPE` TINYINT(1) NOT NULL COMMENT '日志类型：1-操作日志；2-接口日志；3-异常日志',
  `OPERATE_USER` VARCHAR(50) NOT NULL COMMENT '操作用户',
  `EVENT_TIME` VARCHAR(50) NOT NULL COMMENT '事件发生时间',
  `DESCRIPTION` VARCHAR(50) NOT NULL COMMENT '操作描述',
  `REQUEST_PARAM` TEXT DEFAULT NULL COMMENT '操作参数',
  `REQUEST_IP` VARCHAR(50) DEFAULT NULL COMMENT '请求IP',
  `REQUEST_URI` VARCHAR(200) DEFAULT NULL COMMENT '请求URI',
  `REQUEST_METHOD` VARCHAR(50) DEFAULT NULL COMMENT '请求Method',
  `ADDRESS` VARCHAR(200) DEFAULT NULL COMMENT '请求地址',
  `BROWSER` VARCHAR(200) DEFAULT NULL COMMENT '浏览器',
  `COST_TIME` INT(11) DEFAULT NULL COMMENT '请求耗时',
  `EXEC_RESULT` TINYINT(1) NOT NULL COMMENT '执行结果：0-失败；1-成功',
  `EXEC_MESSAGE` TEXT DEFAULT NULL COMMENT '执行信息',
  `EXCEPTION_DETAIL` TEXT DEFAULT NULL COMMENT '异常信息',
  `VERSION` INT(11) NOT NULL DEFAULT 1 COMMENT '乐观锁',
  `CREATE_USER` VARCHAR(20) NOT NULL COMMENT '创建用户',
  `CREATE_TIME` VARCHAR(20) NOT NULL COMMENT '创建时间',
  `CREATE_DATE` VARCHAR(20) NOT NULL COMMENT '创建日期',
  `UPDATE_USER` VARCHAR(20) NOT NULL DEFAULT '' COMMENT '更新用户',
  `UPDATE_TIME` VARCHAR(20) NOT NULL DEFAULT '' COMMENT '更新时间',
  PRIMARY KEY (`ID`) USING BTREE,
  INDEX LOG_TYPE_INDEX (LOG_TYPE),
  INDEX OPERATE_USER_INDEX (OPERATE_USER),
  INDEX EXEC_RESULT_INDEX (EXEC_RESULT)
) ENGINE=INNODB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='日志表';

/*Table structure for table `file` */
DROP TABLE IF EXISTS `file`;
CREATE TABLE `file` (
  `ID` BIGINT(64) UNSIGNED NOT NULL,
  `FILENAME` VARCHAR(250) NOT NULL COMMENT '文件名',
  `ORIGINAL_FILENAME` VARCHAR(250) NOT NULL COMMENT '原文件名称',
  `SIZE` VARCHAR(50) NOT NULL COMMENT '文件大小',
  `FILE_PATH` VARCHAR(500) NOT NULL DEFAULT '' COMMENT '文件路径',
  `DOWNLOAD_URL` VARCHAR(1000) NOT NULL DEFAULT '' COMMENT '下载链接',
  `MD5` VARCHAR(200) NOT NULL COMMENT '备注',
  `FILE_TYPE` VARCHAR(50) NOT NULL DEFAULT '' COMMENT '文件类型',
  `STORAGE_TYPE` VARCHAR(50) NOT NULL COMMENT '存储类型',
  `UPLOAD_TYPE` VARCHAR(50) NOT NULL COMMENT '上传类型',
  `DELETED` TINYINT(1) NOT NULL DEFAULT 0 COMMENT '是否逻辑删除',
  `VERSION` INT(11) NOT NULL DEFAULT 1 COMMENT '乐观锁',
  `CREATE_USER` VARCHAR(20) NOT NULL COMMENT '创建用户',
  `CREATE_TIME` VARCHAR(20) NOT NULL COMMENT '创建时间',
  `CREATE_DATE` VARCHAR(20) NOT NULL COMMENT '创建日期',
  `UPDATE_USER` VARCHAR(20) NOT NULL DEFAULT '' COMMENT '更新用户',
  `UPDATE_TIME` VARCHAR(20) NOT NULL DEFAULT '' COMMENT '更新时间',
  PRIMARY KEY (`ID`) USING BTREE
) ENGINE=INNODB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='文件表';

/*Table structure for table `file_relation` */
DROP TABLE IF EXISTS `file_relation`;
CREATE TABLE `file_relation` (
  `ID` BIGINT(64) UNSIGNED NOT NULL,
  `FILE_ID` BIGINT(64) NOT NULL COMMENT '文件编号',
  `BUSINESS_ID` BIGINT(64) NOT NULL COMMENT '业务编号',
  `VERSION` INT(11) NOT NULL DEFAULT 1 COMMENT '乐观锁',
  `CREATE_USER` VARCHAR(20) NOT NULL COMMENT '创建用户',
  `CREATE_TIME` VARCHAR(20) NOT NULL COMMENT '创建时间',
  `CREATE_DATE` VARCHAR(20) NOT NULL COMMENT '创建日期',
  `UPDATE_USER` VARCHAR(20) NOT NULL DEFAULT '' COMMENT '更新用户',
  `UPDATE_TIME` VARCHAR(20) NOT NULL DEFAULT '' COMMENT '更新时间',
  PRIMARY KEY (`ID`) USING BTREE,
  UNIQUE KEY `KEY_ROLE_PERM` (`FILE_ID`, `BUSINESS_ID`)
) ENGINE=INNODB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='文件与业务对象关联表';

/*Table structure for table `user_pass_log` */
DROP TABLE IF EXISTS `user_pass_log`;
CREATE TABLE `user_pass_log` (
  `ID` BIGINT(64) UNSIGNED NOT NULL,
  `USER_ID` BIGINT(64) NOT NULL COMMENT '用户编号',
  `PASSWORD` VARCHAR(60) NOT NULL COMMENT '密码',
  `UPDATE_TIME` VARCHAR(20) NOT NULL COMMENT '更新时间',
  PRIMARY KEY (`ID`) USING BTREE
) ENGINE=INNODB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='用户密码更新日志表';

/*Table structure for table `user_token_storage` */
DROP TABLE IF EXISTS `user_token_storage`;
CREATE TABLE `user_token_storage` (
  `USER_ID` BIGINT(64) NOT NULL COMMENT '用户编号',
  `LASTED_TOKEN` TEXT NOT NULL COMMENT '用户当前的Token',
  `VERSION` INT(11) NOT NULL COMMENT '版本',
  `REMARK` VARCHAR(100) COMMENT '备注',
  `CREATE_TIME` VARCHAR(20) NOT NULL COMMENT '创建时间',
  `UPDATE_TIME` VARCHAR(20) COMMENT '更新时间',
  PRIMARY KEY (`USER_ID`) USING BTREE
) ENGINE=INNODB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='用户最新令牌记录表';

/*Table structure for table `log_show_flag` */
DROP TABLE IF EXISTS `log_show_flag`;
CREATE TABLE `log_show_flag` (
  `SHOWED` TINYINT(1) NOT NULL COMMENT '接口日志是否展示',
  `URI` VARCHAR(200) NOT NULL COMMENT '请求地址',
  `METHOD` VARCHAR(50) DEFAULT NULL COMMENT '请求方式',
  `CREATE_USER` VARCHAR(20) NOT NULL COMMENT '创建用户',
  `CREATE_TIME` VARCHAR(20) NOT NULL COMMENT '创建时间',
  `UPDATE_USER` VARCHAR(20) NOT NULL DEFAULT '' COMMENT '更新用户',
  `UPDATE_TIME` VARCHAR(20) NOT NULL DEFAULT '' COMMENT '更新时间',
  INDEX `URI_INDEX` (`URI`),
  INDEX `METHOD_INDEX` (`METHOD`),
  UNIQUE KEY `URI_METHOD_KEY` (`URI`, `METHOD`)
) ENGINE=INNODB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='日志展示标识表';

