mapper.select:
SELECT
    ID, PID, PERM_NAME, ICON, SORT, PERM_CODE, ROUTER_PATH, COMPONENT_NAME, COMPONENT_PATH,
    PERM_TYPE, PERM_LEVEL, ENABLED, I_FRAME, CACHE, HIDDEN, CREATE_USER, CREATE_TIME, UPDATE_USER, UPDATE_TIME
FROM permission
WHERE DELETED = 0


    AND (
        PERM_NAME LIKE #{blurryBind} OR PERM_CODE LIKE #{blurryBind} OR
        COMPONENT_NAME LIKE #{blurryBind} OR COMPONENT_PATH LIKE #{blurryBind}
    )


    AND ENABLED = #{enabled}


    AND I_FRAME = #{iFrame}


    AND PERM_TYPE != 3

ORDER BY SORT, PERM_NAME

SELECT * FROM permission WHERE DELETED = 0 AND PERM_TYPE != 3 AND (
    PERM_LEVEL IN (1, 2) OR (
        PERM_LEVEL = 3 AND ID IN (
            SELECT PERM_ID FROM role_permission_relation WHERE ROLE_ID IN (
                SELECT ROLE_ID FROM user_role_relation WHERE USER_ID = #{userId}
            )
        )
    )
) ORDER BY SORT, PERM_NAME
